mapper.insert:
	INSERT
	  INTO
	      MEMBER
	      (
	       MEM_NO
	      ,MEM_ID
	      ,MEM_PWD
	      ,MEM_NAME
	      ,EMAIL
	      ,GENDER
	      ,AGE
	      ,PHONE
	      ,ADDRESS
	      )
    VALUES
          (
           SEQ_MNO.NEXTVAL
          ,#{memId}
          ,#{memPwd}
          ,#{memName}
          ,#{email}
          ,#{gender}
          ,#{age}
          ,#{phone}
          ,#{address}
          )

mapper.update:
UPDATE
      MEMBER
  SET
      MEM_NAME = #{memName}
     ,EMAIL = #{email}
     ,AGE = #{age}
     ,PHONE = #{phone}
     ,ADDRESS = #{address}
     ,GENDER = #{gender}
     ,MODIFY_DATE = SYSDATE
WHERE
      MEM_ID = #{memId}

mapper.delete:
DELETE
       CERT
 WHERE 	
       WHO = #{who}
   AND
       SECRET = #{secret}
